mapper.insert:
insert into item ( itemId
                 , itemName
                 , price
                 , categoryId
                 , uploadFilename
                 , originalFilename)
values ( (select itemId
          from (select LPAD(IFNULL(MAX(itemId), '0') + 1, 5, '0') as itemId
                from item) tmp)
       , #{itemName}
       , #{price}
       , #{categoryId}
       , #{uploadFilename}
       , #{originalFilename})
